import React, {useState} from 'react';
import InputWord from "./inputs/input_word";
import Button from "./buttons/button";

const InputLabel = () => {
    const [word, setWord] = useState('Type word... (:');

    const clearInput = (e) => {
        e.preventDefault();
        setWord('');
    }

    const printWord = (e) => {
        e.preventDefault();
        alert(word);
        setWord(word)
    }



    return (
        <form>
            <Button onClick={printWord} name={'Search'}/>
            <InputWord type='text' value={word} onChange={e => setWord({word: e.target.value})} />
            <input onChange={e => setWord({word: e.target.value})} value={word} />
            <Button onClick= {clearInput}  name={'Clear'}/>
        </form>
    );
};

export default InputLabel;
