import React, {useState} from 'react';
import InputWord from "./inputs/input_word";
import Button from "./buttons/button";

const InputLabel = () => {
    const [word, setWord] = useState('Type word');

    const clearInput = (e) => {
        e.preventDefault();
        alert('Clear');
        setWord('');
    }

    const printWord = (e) => {
        e.preventDefault();
        alert(word);
        setWord(word)
    }

const setValue = (value,e) => {
    setWord(e);

}

    return (
        <form>
            <Button onClick={printWord} name={'Search'}/>
            <InputWord value={word} type='text' onChange={setValue} />
            {/*<input type='text' onChange={e => setWord(e.target.value)} value={word} />*/}
            <Button onClick= {clearInput}  name={'Clear'}/>
        </form>
    );
};

export default InputLabel;
