import React from 'react';
import InputWord from "./inputs/input_word";
import Button from "./buttons/button";

const InputLabel = () => {
    const [word, setWord] = React.useState('');
    const clearInput = (e) => {
        e.preventDefault();
        setWord('');
    }

    const printWord = (e) => {
        e.preventDefault();
        console.log(word);
        setWord(word)
    }

    return (
        <form>
            <Button onClick={printWord} name={'Search'}/>
            <InputWord value={word}/>
            {/*<Button onClick={clearInput} name={'Clean'}/>*/}
        </form>
    );
};

export default InputLabel;
