import React from 'react';
import InputWord from "./inputs/input_word";
import Button from "./buttons/button";

const InputLabel = () => {
    const [word, setWord] = React.useState('');
    const clearInput = (e) => {
        e.preventDefault();
        setWord('');
    }

    const printWord = (e) => {
        e.preventDefault();
        console.log(word);
        setWord(word)
    }

    const clearInputWord = (e) => {
        e.preventDefault();
        setWord('');
    }
const setValue = (e) => {
    e.preventDefault();
    setWord(e.target.value);
}

    return (
        <form>
            <Button onClick={(e)=> {e.preventDefault(); console.log(word)}} name={'Search'}/>
            <InputWord onChange={setValue} value={word}/>
            <Button onClick= {clearInput}  name={'Clear'}/>
        </form>
    );
};

export default InputLabel;
