import React from 'react';
import InputWord from "./inputs/input_word";
import Button from "./buttons/button";

const InputLabel = () => {
    const [word, setWord] = React.useState('');
    const clearInput = (e) => {
        e.preventDefault();
        setWord('');
    }

    const printWord = (e) => {
        e.preventDefault();
        console.log(word);
        setWord(word)
    }

    const setWordValue = (e) => {
        setWord(e.target.value);
    }

    return (
        <form>
            <Button onClick={(e)=> {e.preventDefault(); console.log('Yo')}} name={'Search'}/>
            <InputWord onChange={setWordValue} value={word}/>
            <Button onClick= {(e)=> {e.preventDefault(); console.log('Yo')}}  name={'Clean'}/>
        </form>
    );
};

export default InputLabel;
